mapper.select:
select *
from fbs_budget_dept
where im_cust_no = #{imCustNo}
and DEPT_CORD = #{deptCode}
and PARENT_CODE = #{parentCode}

select *
from fbs_budget_dept
where im_cust_no = #{imCustNo}
and CORP_CODE = #{corpCode}

mapper.update:
update fbs_budget_dept
set budget_flag = #{budgetFlag}
where im_cust_no = #{imCustNo}
and DEPT_CORD = #{deptCode}
and CORP_CODE = #{corpCode}
and PARENT_CODE = #{parentCode}
